// Extract and process incoming Telegram message
const message = $input.first().json.message;
const chatId = message.chat.id;
const userId = message.from.id;
const userName = message.from.first_name + ' ' + (message.from.last_name || '');
const userPhone = message.contact?.phone_number || null;
const messageText = message.text || '';
const messageType = message.photo ? 'photo' : message.document ? 'document' : 'text';

// Extract user context
const userContext = {
  telegramId: userId,
  chatId: chatId,
  userName: userName.trim(),
  phone: userPhone,
  messageText: messageText,
  messageType: messageType,
  timestamp: new Date().toISOString(),
  language: 'ar' // Arabic
};

// Store conversation context for AI agent
const conversationContext = {
  userId: userId,
  chatId: chatId,
  currentMessage: messageText,
  messageHistory: [], // Will be populated from database
  customerData: null, // Will be populated from CRM
  intent: null // Will be determined by AI
};

return {
  userContext,
  conversationContext,
  originalMessage: message
};

// Merge customer data from lookup or creation
const messageData = $input.first().json;
const customerLookup = $input.all().find(item => item.json.success && item.json.customer);
const customerCreation = $input.all().find(item => item.json.success && item.json.data);

let customerData = null;

if (customerLookup) {
  customerData = customerLookup.json.customer;
} else if (customerCreation) {
  customerData = customerCreation.json.data;
}

// Enhanced conversation context with customer data
const enhancedContext = {
  ...messageData.conversationContext,
  customerData: customerData,
  isNewCustomer: !customerLookup,
  customerName: customerData?.firstName + ' ' + (customerData?.lastName || ''),
  customerId: customerData?.id,
  totalPurchases: customerData?.totalPurchases || 0,
  lastOrderDate: customerData?.lastOrderDate || null
};

return {
  ...messageData,
  conversationContext: enhancedContext
};

